import { createStore } from 'vuex';


const store = createStore({
  state: {
    products: [],
  },
  mutations: {
    getProducts() {
      const url = `${process.env.VUE_APP_API}api/${process.env.VUE_APP_PATH}/products/all`;
      this.$http.get(url)
        .then((res) => {
          this.state.products = res.data.products;
        });
    },
  },
  actions: {
    // 在這裡定義你的 actions
  },
  modules: {
    // 在這裡引入其他模組 (如果有)
  },
});

export default store;
